var newMsg = msg;

// Convert the object to an array
newMsg.payload = msg.payload.value;
// Convert date in second to string.
// It time is > 0.
let timeIndex = 0;
let newDateSecond;
let newDate;
let newDateStringLocal;
while ((timeIndex < newMsg.payload.length) &&
       (newMsg.payload[timeIndex].DateTime > 0))
       {
       // Convert second to string for DateTime
       newDateSecond = newMsg.payload[timeIndex].DateTime;
       newDate = new Date(newDateSecond * 1000);
       newDateStringLocal = newDate.toLocaleString('fr-CH');

       // Write DateTime in string format for DataTime
       newMsg.payload[timeIndex].DateTime = newDateStringLocal;

       // If AckDateTime > 0, convert it to String too.
       if(newMsg.payload[timeIndex].AckDateTime > 0){
              // Convert second to string for DateTime
              newDateSecond = newMsg.payload[timeIndex].AckDateTime;
              newDate = new Date(newDateSecond * 1000);
              newDateStringLocal = newDate.toLocaleString('fr-CH');

              // Write DateTime in string format for DataTime
              newMsg.payload[timeIndex].AckDateTime = newDateStringLocal;
       }

        timeIndex++;
        flow.set("FlowTimeIndex", timeIndex);
        flow.set("DateString", newDateStringLocal);
       }

return newMsg;

